async function getJson(){try{const e=await fetch("assets/content/data.json");return await e.json()}catch(e){console.log("Error!",e)}}async function renderItem({number:e,name:a,description:n,baseImage:r,printImage:t}){try{$(".number-text").text(e),$(".container-image img").attr("src",r),$(".title").text(a),$(".description").text(n),$("#fullscreen").css("backgroundImage",`url(${r})`)}catch(e){console.log("Error!",e)}}async function renderList(e){e.map(({number:e,name:a})=>{$(".modal-list").append(`<li class="radio-item">\n    <a href=""> <span class="number-radio">${e} - </span>${a}</a>\n  </li>`)})}$(document).ready(async()=>{const e=await getJson();let a=e[0];await renderItem(a),await renderList(e),$(".next").click(async()=>{a=e[a.number%e.length],await renderItem(a),$(".remove-areas").hide(),$(".print-areas").show()}),$(".previous").click(async()=>{console.log((a.number+1)%e.length),a=e[a.number%e.length],await renderItem(a),$(".remove-areas").hide(),$(".print-areas").show()}),$("header .ham_menu").click(()=>{$(".modal").toggle(),$(".ham_menu").toggleClass("active")}),$("#fullscreen .ham_menu").click(()=>{$("#fullscreen").fadeOut()}),$(".container-image").click(()=>{$("#fullscreen").fadeIn()}),$(".print-areas").click(()=>{$("#fullscreen").css("background-image",`url(${a.printImage})`),$(".print-areas").hide(),$(".remove-areas").show()}),$(".remove-areas").click(()=>{$("#fullscreen").css("background-image",`url(${a.baseImage})`),$(".remove-areas").hide(),$(".print-areas").show()})});