async function getJson(){try{const e=await fetch("assets/content/data.json"),t=await e.json();console.log(t)}catch(e){console.log("Error!",e)}}$(document).ready(async()=>{const e=getJson();$(".number-text").text(e[0].number),$(".container-image img").attr("src",e[0].baseImage),$(".title").text(e[0].title),$(".description").text(e[0].description),$("header .ham_menu").click(()=>{$(".modal").toggle(),$(".ham_menu").toggleClass("active")}),$("#fullscreen .ham_menu").click(()=>{$("#fullscreen").fadeOut()}),$(".container-image").click(()=>{$("#fullscreen").fadeIn()})});